import React, { Component } from 'react';
import './Validator.css';

class Validator extends Component {
  constructor() {
    super()
    this.state = { // set originating state of validator component
      email: "",
      password: "",
      passwordConfirm: "",
      valid: false
    };

    // According to Docs this binding is necessary to make `this` work in the callback
    this.handlePassword = this.handlePassword.bind(this)
    this.handleRetype = this.handleRetype.bind(this)
    this.handleCheck = this.handleCheck.bind(this)
  }

  handlePassword(e) {
    this.setState({password: e.target.value})
  }
  handleRetype(e) {
    this.setState({passwordConfirm: e.target.value})
  }
  handleCheck() {
    if(this.state.password == this.state.passwordConfirm) {
      this.setState({valid:true})
    }
    else {
      this.setState({valid:false})
    }

  }

  render() {
    return (
      <div className="form">
        <h1>Sign Up</h1>
        <input type="text" placeholder="Email" />
        <input type="password" placeholder="Password" value={this.state.value} onChange={this.handlePassword} />
        <input type="password" placeholder="Password confirm" value={this.state.value} onChange={this.handleRetype} />
        <button type="submit" value="submit" onClick={this.handleCheck}>Submit</button>
        <p>{this.state.valid}</p>
      </div>
    );
  }
}

export default Validator;
